mapper.select:
select *
from (
	 select t.*, rownum rn
	 from (
			select rno, bno, reply, replyer
					, to_char(replyDate,'yyyy-MM-dd') replyDate
					, updateDate
			  from tbl_reply
			 where bno = #{bno}
		  order by rno desc
		) t
	)
	where rn between #{cri.startNo} and #{cri.endNo}

mapper.update:
 update tbl_reply
	set reply = #{reply}
	, updateDate = sysdate
  where rno = #{rno}

mapper.select:
select count(*)
  from tbl_reply
  where bno=#{bno}
